import { useHistory } from 'react-router-dom';
import useFormContext from "../hooks/useFormContext";
import UserInfo from "./UserInfo";

const FormInputs = () => {
    const { page, setPage } = useFormContext();
    const history = useHistory();

    function handleNext() {
        if (page === 0) {  // Assuming page 0 is the Get Started page
            history.push("/choose-rec-method");  // Redirect to ChooseRecMethod after Get Started
        } else {
            setPage(oldPage => oldPage + 1);
        }
    }

    return (
        <div className="all-forms">
            {page === 0 ? <UserInfo /> : null}
            <div className="buttons prev--next">
                <button type='button' onClick={handleNext}>
                    {page === 0 ? "Next" : "Get Recommendations"}
                </button>
            </div>
        </div>
    );
};

export default FormInputs;
