import Title from './components/Title';
import ChooseRecMethod from './components/ChooseRecMethod';  // Import the new component
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom/cjs/react-router-dom.min';
import FormInputs from './components/FormInputs';
import Recommendations from './components/Recommendations';
import FinalRecommendations from './components/FinalRecommendations';
import RecommendationsByDescription from './components/RecommendationsByDescription';  // If you create this component
import { FormProvider } from './context/FormContext';

function App() {
    return (
        <Router>
            <div className="container">
                <div className="main-body">
                    <Switch>
                        <Route exact path="/">
                            <Title />
                        </Route>
                        <Route exact path="/get-started">
                            <FormProvider>
                                <FormInputs />
                            </FormProvider>
                        </Route>
                        <Route exact path="/choose-recommendation-method">
                            <ChooseRecMethod />  {/* <-- Add this route */}
                        </Route>
                        <Route exact path="/recommendations">
                            <Recommendations />
                        </Route>
                        <Route exact path="/recommendations-description">
                            <RecommendationsByDescription />  {/* <-- Add this route */}
                        </Route>
                        <Route exact path="/final-recommendations">
                            <FinalRecommendations />
                        </Route>
                    </Switch>
                </div>
            </div>
        </Router>
    );
}

export default App;
