import { useState } from "react";
import useFormContext from "../hooks/useFormContext";
const Genres = () => {
    const genres = [
        '', 'art', 'biography', 'books', 'business', 'chick-lit', 'christian', 'classics',
        'comics', 'contemporary', 'cookbooks', 'crime', 'fantasy', 'fiction',
        'gay-and-lesbian', 'graphic-novels', 'historical-fiction', 'history',
        'horror', 'humor-and-comedy', 'manga', 'memoir', 'music', 'mystery',
        'nonfiction', 'paranormal', 'philosophy', 'poetry', 'psychology', 'religion',
        'romance', 'science', 'science-fiction', 'self-help', 'spirituality', 'sports',
        'suspense', 'thriller', 'travel', 'young-adult'
      ];

    const {data, handleChange} = useFormContext();
    const BookInputData = data;
    return ( 
        <div className="login--form">
            <br />
           
            <h2>
                Select your Favorite Genres
            </h2>
         
            <form action="" className=""><br />
            <p>Select your first favorite genre</p>
            <label htmlFor="genre-dropdown">Choose a genre:</label>
                <select 
                    id="genre-dropdown" 
                    name="genre1"
                    value = {BookInputData.genre1}
                    onChange={handleChange}
                >
                    {genres.map((element, index) => (
                        <option 
                            key={index} 
                            value={element} 
                            id="genre-dropdown" 
                        >
                            {element}
                        </option>
                    ))}
                </select>

            <p>Select your second favorite genre</p>
            <label htmlFor="genre-dropdown">Choose a genre:</label>
                <select 
                    id="genre-dropdown" 
                    name="genre2"
                    value = {BookInputData.genre2}
                    onChange={handleChange}
                >
                    {genres.map((element, index) => (
                        <option 
                            key={index} 
                            value={element} 
                            id="genre-dropdown" 
                        >
                            {element}
                        </option>
                    ))}
                </select>

            <p>Select your third favorite genre</p>
            <label htmlFor="genre-dropdown">Choose a genre:</label>
                <select 
                    id="genre-dropdown" 
                    name="genre3"
                    value = {BookInputData.genre3}
                    onChange={handleChange}
                >
                    {genres.map((element, index) => (
                        <option 
                            key={index} 
                            value={element} 
                            id="genre-dropdown" 
                        >
                            {element}
                        </option>
                    ))}
                </select>

            <p>Select your fourth favorite genre</p>
            <label htmlFor="genre-dropdown">Choose a genre:</label>
                <select 
                    id="genre-dropdown" 
                    name="genre3"
                    value = {BookInputData.genre3}
                    onChange={handleChange}
                >
                    {genres.map((element, index) => (
                        <option 
                            key={index} 
                            value={element} 
                            id="genre-dropdown" 
                        >
                            {element}
                        </option>
                    ))}
                </select>
            <p>Select your fifth favorite genre</p>
            <label htmlFor="genre-dropdown">Choose a genre:</label>
                <select 
                    id="genre-dropdown" 
                    name="genre3"
                    value = {BookInputData.genre3}
                    onChange={handleChange}
                >
                    {genres.map((element, index) => (
                        <option 
                            key={index} 
                            value={element} 
                            id="genre-dropdown" 
                        >
                            {element}
                        </option>
                    ))}
                </select>



            </form>
        </div>
     );
}
 
export default Genres;
