import { FormProvider } from './context/FormContext';

import Title from './components/Title';
import Carousel from './components/Carousel';

import Genres from './components/Genres';
import FormInputs from './components/FormInputs';

import Recommendations from './components/Recommendations';  
import FinalRecommendations from './components/FinalRecommendations'; 
import ChooseRecMethod from './components/ChooseRecMethod';  
import DescriptionInput from './components/DescriptionInput';

function App() {
  return (
      <Router>
          <div className="container">
              <div className="main-body">
                  <div className="top">
                      <Carousel />  {/* Top carousel */}
                  </div>
                  <Switch>
                      <Route exact path="/">
                          <Title />
                      </Route>
                      <Route exact path="/get-started">
                          <FormProvider>
                              <FormInputs />
                          </FormProvider>
                      </Route>
                      <Route exact path="/choose-genres">
                          <Genres />
                      </Route>
                      <Route exact path="/final-recommendations">
                          <FinalRecommendations />
                      </Route>
                      <Route exact path="/choose-rec-method">
                          <ChooseRecMethod />  
                      </Route>
                      <Route exact path="/description-input">  
                          <DescriptionInput />
                      </Route>
                      <Route exact path="/recommendations">
                          <Recommendations />
                      </Route>
                  </Switch>
                  <div className="bottom">
                      <Carousel />  {/* Bottom carousel */}
                  </div>
              </div>
          </div>
      </Router>
  );
}

export default App;
