import { useState } from "react";
import { useHistory } from "react-router-dom";

const DescriptionInput = () => {
    const [description, setDescription] = useState('');
    const history = useHistory();

    // Handle input change
    const handleChange = (event) => {
        setDescription(event.target.value);
    };

    // Handle form submission or moving to recommendations
    const handleSubmit = () => {
        // You can process the description input here, such as saving it to context or state.
        console.log("User description: ", description);

        // After submitting the description, navigate to recommendations
        history.push("/recommendations");
    };

    return (
        <div className="login--form description-input">
            <h2>Describe the kind of books you want</h2>
            <br></br>
            <textarea
                value={description}
                onChange={handleChange}
                placeholder="Type a description of the kinds of books you're interested in..."
                rows="15"
                cols="75"
            />
            <br /><br />
            <button onClick={handleSubmit}>Get Recommendations</button>
            <div className="prev--next gb">
                <button id="genre-button" type="button" onClick={handleSubmit}>Get Recommendations</button>
                <button id="genre-button" type="button" onClick={handleNext}>SUBMIT</button> 
            </div>
        </div>
    );
};

export default DescriptionInput;
