import { useHistory } from 'react-router-dom';
import useFormContext from "../hooks/useFormContext";
import Genres from "./Genres";
import UserInfo from "./UserInfo";

const FormInputs = () => {
    const { title, page, setPage } = useFormContext();
    const history = useHistory();

    const display = {
        0: <UserInfo />,
        1: <Genres />
    };

    function handlePrev() {
        setPage(oldPage => oldPage - 1);
    }

    function handleNext() {
        if (page === 0) {
            history.push("/choose-rec-method");  // Redirect after user info is submitted
        }
        // Otherwise, proceed to the next page (e.g., Genres)
        else if (page === 1) {
            history.push("/recommendations");  // If genres are selected, go to recommendations
        } else {
            setPage(oldPage => oldPage + 1);
        }
    }

    return (
        <div className="all-forms">
            {display[page]}
            <div className="buttons prev--next">
                {page !== 0 && <button type='button' onClick={handlePrev}>PREV</button>}
                <button type='button' onClick={handleNext}>
                    {page === Object.keys(title).length - 1 ? "Get Recommendations" : "NEXT"}
                </button>
            </div>
        </div>
    );
};

export default FormInputs;
