import React from 'react';
import { useHistory } from 'react-router-dom';

const ChooseRecMethod = () => {
    const history = useHistory();

    // Navigate to the recommendations by genre page
    const handleGenreClick = () => {
        history.push('/recommendations');  // Modify this route if necessary
    };

    // Navigate to the recommendations by description page
    const handleDescriptionClick = () => {
        history.push('/recommendations-description');  // Modify this route if necessary
    };

    return (
        <div>
            <h1>Choose Recommendation Method</h1>
            <p>Please select how you'd like to get your recommendations:</p>
            <button onClick={handleGenreClick}>By Genre</button>
            <button onClick={handleDescriptionClick}>By Description</button>
        </div>
    );
};

export default ChooseRecMethod;
