import book from "../books.png";
import fetchBookCover from "../fetchBooks";
import BookImage from "./BookImage";
import { useState, useEffect } from "react";


const Carousel = () => {
  const bookTitles = [
    "The Seven Husbands of Evelyn Hugo",
    "Every Last One",
    "The Thing Around Your Neck",
    "The Dictionary of Obscure Sorrows",
    "The Silent Patient",
    "My Year of Rest and Relaxation",
    "On Earth We're Briefly Gorgeous",
    "A Little Life",
    "Babel",
    "And then there were none",
    "The Hunger Games",
    "Mockingjay",
    "The Three-Body Problem",
    "The Stranger",
    "Purple Hibiscus",
    "The Song of Achilles",
    "The Bell Jar",
    "Open Water",
    "Normal People",
    "The Secret History",
    "Yinka, Where’s Your Huzzband?",
    "None of This Is True: A Novel",
    "Stay with Me (Ayobami Adebayo)",
    "1984",
    "In the Dream House"
  ];
  const [bookURLs, setBookURLs] = useState([]);
  
  useEffect(() => {
      const fetchBookCovers = async () => {
        const promises = bookTitles.map(async (title) => {
          const imageUrl = await fetchBookCover(title);
          return imageUrl;
        });
        const images = await Promise.all(promises);
        setBookURLs(images);
      };
  
      fetchBookCovers();
    }, []);

  console.log(bookURLs);
  let bookImageElements = bookURLs.map((url) => createBookImageElements(url));
  function createBookImageElements(url){
      return <BookImage 
        key = {url}
        source = {url}
      />
  }
     
    return (
        <div className="carousel">
          {bookImageElements}
        </div>

      );
}
 
export default Carousel;
