import useFormContext from "../hooks/useFormContext";
import { fetchRecommendationsByGenre, fetchRecommendationsByDescription } from "../api";  


const FinalRecommendations = () => {
    const {data} = useFormContext();
    const history = useHistory();
    const chosen = data.chosenIndices

    function handleBookSelection(book) {
        setSelectedBooks(prevBooks => {
            if (prevBooks.includes(book)) {
                return prevBooks.filter(b => b !== book);
            }
            if (prevBooks.length < 3) {
                return [...prevBooks, book];
            }
            return prevBooks;
        });
    }

    function handleSubmit() {
        if (selectedBooks.length === 3) {
            console.log("Selected books:", selectedBooks);
        } else {
            alert("Please select exactly 3 books.");
        }
    }

    return (
        <div>
            <h2>Choose 3 Books</h2>
            <ul>
                {/* Assuming recommended books are passed as props or from context */}
                {["Book1", "Book2", "Book3"].map(book => (
                    <li key={book}>
                        <input
                            type="checkbox"
                            checked={selectedBooks.includes(book)}
                            onChange={() => handleBookSelection(book)}
                        />
                        {book}
                    </li>
                ))}
            </ul>
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
};

export default FinalRecommendations;
