// Function to fetch book cover image based on book title
export default async function fetchBookCover(bookTitle) {
    try {
        // Replace with your Google Books API key
        const apiKey = 'AIzaSyCr2YD6JGrjgQKBKN3Lf8LktV7DoC2oogE';
        const apiUrl = `https://www.googleapis.com/books/v1/volumes?q=${encodeURIComponent(bookTitle)}&key=${apiKey}&maxResults=1&`;
    
        const response = await fetch(apiUrl);
        const data = await response.json();
   
        if (data.items && data.items.length > 0) {
          const book = data.items[0];
          if (book.volumeInfo && book.volumeInfo.imageLinks && book.volumeInfo.imageLinks.thumbnail) {
              const urlWithCurl = book.volumeInfo.imageLinks.smallThumbnail;
              return urlWithCurl.replace("edge=curl&", "");
          }
        }
        return null; // Handle case where no image is available
      } catch (error) {
        console.error('Error fetching book cover:', error);
        return null;
    }
  }
