import React, { createContext, useState } from "react";

const FormContext = createContext({});

export const FormProvider = ({ children }) => {
    const title = {
        0: 'User-Info',
        1: 'User-Genres',
    };
    const [page, setPage] = useState(0);
    
    const [data, setData] = useState({
        fname: "",
        lname: "",
        dob: "",
        email: "",
        genre1: "",
        genre2: "",
        genre3: "",
        genre4: "", // New field for the fourth genre
        genre5: "", // New field for the fifth genre
        favoriteAuthors: "", // Optional: New field for favorite authors
        readingHabits: "", // Optional: New field for reading habits
        consent: false // Checkbox for data privacy consent
    });

    const handleChange = (event) => {
        const { name, value, type, checked } = event.target;
        setData((oldData) => ({
            ...oldData,
            [name]: type === "checkbox" ? checked : value,
        }));
    };

    const resetForm = () => {
        setData({
            fname: "",
            lname: "",
            dob: "",
            email: "",
            genre1: "",
            genre2: "",
            genre3: "",
            genre4: "", // Reset for the fourth genre
            genre5: "", // Reset for the fifth genre
            favoriteAuthors: "",
            readingHabits: "",
            consent: false
        });
        setPage(0);
    };

    return (
        <FormContext.Provider value={{ title, page, setPage, data, setData, handleChange, resetForm }}>
            {children}
        </FormContext.Provider>
    );
};

export default FormContext;
